      xAxis: {
        min: -25,
        max: 5,
        grid: {
          line: {
            style: {
              stroke: '#eee',
            },
          },
        },
      },
      quadrant: {
        xBaseline: 0,
        yBaseline: 0,
        labels: [
          {
            content: 'Male decrease,\nfemale increase',
          },
          {
            content: 'Female decrease,\nmale increase',
          },
          {
            content: 'Female & male decrease',
          },
          {
            content: 'Female &\n male increase',
          },
        ],
      },
    });
    scatterPlot.render();
  });

